--
-- Name: crm_activity_report; Type: VIEW; Schema: public; Owner: admin
--

CREATE VIEW public.crm_activity_report AS
 SELECT m.id,
    m.subtype_id,
    m.mail_activity_type_id,
    m.author_id,
    m.date,
    m.subject,
    l.id AS lead_id,
    l.user_id,
    l.team_id,
    l.country_id,
    l.company_id,
    l.stage_id,
    l.partner_id,
    l.type AS lead_type,
    l.active,
    l.probability
   FROM (public.mail_message m
     JOIN public.crm_lead l ON ((m.res_id = l.id)))
  WHERE (((m.model)::text = 'crm.lead'::text) AND (m.mail_activity_type_id IS NOT NULL));


